import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [contacts, setContacts] = useState([]);

  useEffect(() => {
    // Fetch the list of contacts from the backend
  }, []);

  const handleCreateContact = () => {
    // Create a new contact with the contact name that the user entered
    // Add the new contact to the list of contacts
    setContacts([...contacts, { contactName: name }]);
  };

  const handleDeleteContact = (contact) => {
    // Delete the contact from the list of contacts
    setContacts(contacts.filter((c) => c !== contact));
  };

  const handleEditContact = (contact) => {
    // Open a modal to allow the user to edit the contact
    setEditContact(contact);
  };

  const [editContact, setEditContact] = useState(null);

  const [nameNumbers, setNameNumbers] = useState([]);

  return (
    <div>
      <h1>Contactor</h1>
      <div className="container">
        <h2>Contacts</h2>
        <ul>
          {contacts.map((contact) => (
            <li key={contact.contactName}>
              <button onClick={() => handleDeleteContact(contact)}>Delete</button>
              <button onClick={() => {
                setEditContact(contact);
              }}>Edit</button>
              {contact.contactName}
            </li>
          ))}
        </ul>
        <input type="text" placeholder="Enter contact name" value={name} onChange={(event) => setName(event.target.value)} />
        <button onClick={handleCreateContact}>Create Contact</button>
        {editContact && (
          <div className="edit-contact-modal">
            <h2>Edit Contact</h2>
            <input
              type="text"
              placeholder="Enter contact name"
              value={editContact.contactName}
              onChange={(event) => setEditContact({ ...editContact, contactName: event.target.value })}
            />
            <div className="name-number-list">
              {nameNumbers.map((nameNumber) => (
                <div key={nameNumber.id}>
                  <input
                    type="text"
                    placeholder="Enter name"
                    value={nameNumber.name}
                    onChange={(event) => setNameNumbers({ ...nameNumbers, name: event.target.value })}
                  />
                  <input
                    type="text"
                    placeholder="Enter number"
                    value={nameNumber.number}
                    onChange={(event) => setNameNumbers({ ...nameNumbers, number: event.target.value })}
                  />
                  <button onClick={() => {
                    setNameNumbers(nameNumbers.filter((n) => n.id !== nameNumber.id));
                  }}>Delete</button>
                </div>
              ))}
            </div>
            <button onClick={() => {
              setEditContact(null);
            }}>Cancel</button>
            <button onClick={() => {
              setEditContact(null);
            }}>Update</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
