// EditContactModal.js
import React, { useState } from 'react';

function EditContactModal({ contact, onSave, onClose }) {
  const [newName, setNewName] = useState(contact.contactName);
  const [newNumbers, setNewNumbers] = useState(contact.numbers || []);

  const handleAddNumber = () => {
    setNewNumbers([...newNumbers, '']);
  };

  const handleDeleteNumber = (index) => {
    const updatedNumbers = newNumbers.filter((_, i) => i !== index);
    setNewNumbers(updatedNumbers);
  };

  const handleSave = () => {
    onSave({
      ...contact,
      contactName: newName,
      numbers: newNumbers,
    });
    onClose();
  };

  return (
    <div className="modal">
      <h2>Edit Contact</h2>
      <label>Contact Name:</label>
      <input type="text" value={newName} onChange={(e) => setNewName(e.target.value)} />
      <h3>Numbers:</h3>
      <ul>
        {newNumbers.map((number, index) => (
          <li key={index}>
            <input
              type="text"
              value={number}
              onChange={(e) => {
                const updatedNumbers = [...newNumbers];
                updatedNumbers[index] = e.target.value;
                setNewNumbers(updatedNumbers);
              }}
            />
            <button onClick={() => handleDeleteNumber(index)}>Delete</button>
          </li>
        ))}
      </ul>
      <button onClick={handleAddNumber}>Add Number</button>
      <button onClick={handleSave}>Save</button>
      <button onClick={onClose}>Cancel</button>
    </div>
  );
}

export default EditContactModal;
